contracts/CrossChainSwapManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "./HashTimeLockContract.sol";

/**
 * @title CrossChainSwapManager
 * @dev Manages cross-chain swaps using Hash Time Lock Contracts
 */
contract CrossChainSwapManager is ReentrancyGuard, Ownable, Pausable {
    using SafeERC20 for IERC20;

    HashTimeLockContract public immutable htlc;

    // Supported tokens for swapping
    mapping(address => bool) public supportedTokens;

    // Swap fees (in basis points, e.g., 50 = 0.5%)
    uint256 public swapFeeBasisPoints = 50;
    uint256 public constant MAX_FEE_BASIS_POINTS = 1000; // 10% max

    // Fee recipient
    address public feeRecipient;

    // Minimum swap amounts per token
    mapping(address => uint256) public minSwapAmounts;

    // Maximum swap amounts per token
    mapping(address => uint256) public maxSwapAmounts;

    // Events
    event SwapInitiated(
        bytes32 indexed swapId,
        address indexed initiator,
        address indexed token,
        uint256 amount,
        bytes32 hashLock,
        uint256 timelock,
        string targetChain,
        string targetAddress
    );

    event SwapCompleted(
        bytes32 indexed swapId,
        address indexed recipient,
        bytes32 secret
    );

    event SwapRefunded(
        bytes32 indexed swapId,
        address indexed recipient
    );

    event TokenSupportUpdated(address indexed token, bool supported);
    event SwapLimitsUpdated(address indexed token, uint256 minAmount, uint256 maxAmount);
    event FeeUpdated(uint256 oldFee, uint256 newFee);
    event FeeRecipientUpdated(address indexed oldRecipient, address indexed newRecipient);

    struct SwapInfo {
        address token;
        uint256 amount;
        address initiator;
        string targetChain;
        string targetAddress;
        bool completed;
        bool refunded;
    }

    mapping(bytes32 => SwapInfo) public swaps;

    constructor(address _htlc) {
        require(_htlc != address(0), "Invalid HTLC address");
        htlc = HashTimeLockContract(_htlc);
        feeRecipient = msg.sender;
    }

    /**
     * @dev Initiate a cross-chain swap
     */
    function initiateSwap(
        address token,
        uint256 amount,
        bytes32 hashLock,
        uint256 timelock,
        string calldata targetChain,
        string calldata targetAddress
    ) external payable nonReentrant whenNotPaused returns (bytes32 swapId) {
        require(supportedTokens[token] || token == address(0), "Token not supported");
        require(amount > 0, "Amount must be greater than 0");
        require(timelock > block.timestamp, "Timelock must be in the future");
        require(timelock <= block.timestamp + 24 hours, "Timelock too far in future");
        require(bytes(targetChain).length > 0, "Target chain required");
        require(bytes(targetAddress).length > 0, "Target address required");

        // Check swap limits
        if (token != address(0)) {
            require(amount >= minSwapAmounts[token], "Amount below minimum");
            require(maxSwapAmounts[token] == 0 || amount <= maxSwapAmounts[token], "Amount above maximum");
        }

        // Calculate fee
        uint256 fee = (amount * swapFeeBasisPoints) / 10000;
        uint256 netAmount = amount - fee;

        if (token == address(0)) {
            // ETH swap
            require(msg.value == amount, "Incorrect ETH amount");

            // Create HTLC for net amount (3 parameters: recipient, hashLock, timelock)
            swapId = htlc.newContract{value: netAmount}(
                payable(msg.sender),
                hashLock,
                timelock
            );

            // Transfer fee to fee recipient
            if (fee > 0) {
                payable(feeRecipient).transfer(fee);
            }
        } else {
            // ERC20 token swap
            require(msg.value == 0, "No ETH should be sent for token swaps");

            // Transfer tokens from user to this contract
            IERC20(token).safeTransferFrom(msg.sender, address(this), amount);

            // For ERC20 tokens, we need to handle the HTLC creation differently
            // Option 1: If your HTLC has a separate method for ERC20
            // Option 2: If your HTLC uses a unified approach but needs token transfer first

            // Transfer net amount to HTLC contract
            IERC20(token).safeTransfer(address(htlc), netAmount);

            // Create HTLC (assuming it can handle ERC20 if tokens are already transferred)
            swapId = htlc.newContract(
                payable(msg.sender),
                hashLock,
                timelock
            );

            // Transfer fee to fee recipient
            if (fee > 0) {
                IERC20(token).safeTransfer(feeRecipient, fee);
            }
        }

        // Store swap info
        swaps[swapId] = SwapInfo({
            token: token,
            amount: netAmount,
            initiator: msg.sender,
            targetChain: targetChain,
            targetAddress: targetAddress,
            completed: false,
            refunded: false
        });

        emit SwapInitiated(
            swapId,
            msg.sender,
            token,
            netAmount,
            hashLock,
            timelock,
            targetChain,
            targetAddress
        );
    }

    /**
     * @dev Complete a swap by revealing the secret
     */
    function completeSwap(bytes32 swapId, string calldata secret) external nonReentrant {
        SwapInfo storage swap = swaps[swapId];
        require(swap.amount > 0, "Swap not found");
        require(!swap.completed, "Swap already completed");
        require(!swap.refunded, "Swap already refunded");

        // Complete the HTLC
        htlc.withdraw(swapId, secret);

        swap.completed = true;
        emit SwapCompleted(swapId, msg.sender, keccak256(abi.encodePacked(secret)));
    }

    /**
     * @dev Refund a swap after timelock expiry
     */
    function refundSwap(bytes32 swapId) external nonReentrant {
        SwapInfo storage swap = swaps[swapId];
        require(swap.amount > 0, "Swap not found");
        require(!swap.completed, "Swap already completed");
        require(!swap.refunded, "Swap already refunded");
        require(msg.sender == swap.initiator, "Only initiator can refund");

        // Refund the HTLC
        htlc.refund(swapId);

        swap.refunded = true;
        emit SwapRefunded(swapId, msg.sender);
    }

    /**
     * @dev Get swap information
     */
    function getSwap(bytes32 swapId) external view returns (
        address token,
        uint256 amount,
        address initiator,
        string memory targetChain,
        string memory targetAddress,
        bool completed,
        bool refunded
    ) {
        SwapInfo storage swap = swaps[swapId];
        return (
            swap.token,
            swap.amount,
            swap.initiator,
            swap.targetChain,
            swap.targetAddress,
            swap.completed,
            swap.refunded
        );
    }

    // Admin functions
    function setSupportedToken(address token, bool supported) external onlyOwner {
        supportedTokens[token] = supported;
        emit TokenSupportUpdated(token, supported);
    }

    function setSwapLimits(address token, uint256 minAmount, uint256 maxAmount) external onlyOwner {
        require(maxAmount == 0 || maxAmount >= minAmount, "Invalid limits");
        minSwapAmounts[token] = minAmount;
        maxSwapAmounts[token] = maxAmount;
        emit SwapLimitsUpdated(token, minAmount, maxAmount);
    }

    function setSwapFee(uint256 newFeeBasisPoints) external onlyOwner {
        require(newFeeBasisPoints <= MAX_FEE_BASIS_POINTS, "Fee too high");
        uint256 oldFee = swapFeeBasisPoints;
        swapFeeBasisPoints = newFeeBasisPoints;
        emit FeeUpdated(oldFee, newFeeBasisPoints);
    }

    function setFeeRecipient(address newFeeRecipient) external onlyOwner {
        require(newFeeRecipient != address(0), "Invalid fee recipient");
        address oldRecipient = feeRecipient;
        feeRecipient = newFeeRecipient;
        emit FeeRecipientUpdated(oldRecipient, newFeeRecipient);
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    // Emergency functions
    function emergencyWithdraw(address token, uint256 amount) external onlyOwner {
        if (token == address(0)) {
            payable(owner()).transfer(amount);
        } else {
            IERC20(token).safeTransfer(owner(), amount);
        }
    }

    // Alternative methods if your HTLC contract has different method signatures
    // Uncomment and modify these if needed:

    /*
    function initiateERC20Swap(
        address token,
        uint256 amount,
        bytes32 hashLock,
        uint256 timelock,
        string calldata targetChain,
        string calldata targetAddress
    ) external nonReentrant whenNotPaused returns (bytes32 swapId) {
        // Implementation for contracts with separate ERC20 methods
        // You would need to adjust this based on your HTLC interface
    }
    */
}

contracts/HashTimeLockContract.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title HashTimeLockContract
 * @dev Implements Hash Time Locked Contracts for cross-chain atomic swaps
 */
contract HashTimeLockContract is ReentrancyGuard {
    using SafeERC20 for IERC20;

    struct HTLC {
        address sender;
        address receiver;
        address tokenContract;
        uint256 amount;
        bytes32 hashLock;
        uint256 timelock;
        bool withdrawn;
        bool refunded;
        string preimage;
    }

    mapping(bytes32 => HTLC) public contracts;
    mapping(address => bytes32[]) public userContracts;

    event HTLCNew(
        bytes32 indexed contractId,
        address indexed sender,
        address indexed receiver,
        address tokenContract,
        uint256 amount,
        bytes32 hashLock,
        uint256 timelock
    );

    event HTLCWithdraw(bytes32 indexed contractId, address indexed receiver, string preimage);
    event HTLCRefund(bytes32 indexed contractId, address indexed sender);

    modifier fundsSent() {
        require(msg.value > 0, "No funds sent");
        _;
    }

    modifier futureTimelock(uint256 _time) {
        require(_time > block.timestamp, "Timelock must be in the future");
        _;
    }

    modifier contractExists(bytes32 _contractId) {
        require(hasContract(_contractId), "Contract does not exist");
        _;
    }

    modifier hashlockMatches(bytes32 _contractId, string memory _preimage) {
        require(
            contracts[_contractId].hashLock == keccak256(abi.encodePacked(_preimage)),
            "Hash lock does not match preimage"
        );
        _;
    }

    modifier withdrawable(bytes32 _contractId) {
        require(contracts[_contractId].receiver == msg.sender, "Not authorized to withdraw");
        require(contracts[_contractId].withdrawn == false, "Already withdrawn");
        require(contracts[_contractId].timelock > block.timestamp, "Timelock has expired");
        _;
    }

    modifier refundable(bytes32 _contractId) {
        require(contracts[_contractId].sender == msg.sender, "Not authorized to refund");
        require(contracts[_contractId].refunded == false, "Already refunded");
        require(contracts[_contractId].withdrawn == false, "Already withdrawn");
        require(contracts[_contractId].timelock <= block.timestamp, "Timelock not yet expired");
        _;
    }

    /**
     * @dev Create a new HTLC for ETH
     * @param _receiver Address of the receiver
     * @param _hashLock Hash of the secret
     * @param _timelock Unix timestamp when the lock expires
     * @return contractId The ID of the created contract
     */
    function newContract(
        address _receiver,
        bytes32 _hashLock,
        uint256 _timelock
    )
        public
        payable
        fundsSent
        futureTimelock(_timelock)
        returns (bytes32 contractId)
    {
        contractId = keccak256(
            abi.encodePacked(
                msg.sender,
                _receiver,
                msg.value,
                _hashLock,
                _timelock
            )
        );

        require(!hasContract(contractId), "Contract already exists");

        contracts[contractId] = HTLC(
            msg.sender,
            _receiver,
            address(0), // ETH
            msg.value,
            _hashLock,
            _timelock,
            false,
            false,
            ""
        );

        userContracts[msg.sender].push(contractId);
        userContracts[_receiver].push(contractId);

        emit HTLCNew(
            contractId,
            msg.sender,
            _receiver,
            address(0),
            msg.value,
            _hashLock,
            _timelock
        );
    }

    /**
     * @dev Create a new HTLC for ERC20 tokens
     * @param _receiver Address of the receiver
     * @param _hashLock Hash of the secret
     * @param _timelock Unix timestamp when the lock expires
     * @param _tokenContract Address of the ERC20 token contract
     * @param _amount Amount of tokens to lock
     * @return contractId The ID of the created contract
     */
    function newContractERC20(
        address _receiver,
        bytes32 _hashLock,
        uint256 _timelock,
        address _tokenContract,
        uint256 _amount
    )
        public
        futureTimelock(_timelock)
        returns (bytes32 contractId)
    {
        require(_amount > 0, "Amount must be greater than 0");
        require(_tokenContract != address(0), "Invalid token contract");

        contractId = keccak256(
            abi.encodePacked(
                msg.sender,
                _receiver,
                _amount,
                _hashLock,
                _timelock,
                _tokenContract
            )
        );

        require(!hasContract(contractId), "Contract already exists");

        // Transfer tokens to this contract
        IERC20(_tokenContract).safeTransferFrom(msg.sender, address(this), _amount);

        contracts[contractId] = HTLC(
            msg.sender,
            _receiver,
            _tokenContract,
            _amount,
            _hashLock,
            _timelock,
            false,
            false,
            ""
        );

        userContracts[msg.sender].push(contractId);
        userContracts[_receiver].push(contractId);

        emit HTLCNew(
            contractId,
            msg.sender,
            _receiver,
            _tokenContract,
            _amount,
            _hashLock,
            _timelock
        );
    }

    /**
     * @dev Withdraw funds from an HTLC by providing the preimage
     * @param _contractId ID of the HTLC
     * @param _preimage The secret that hashes to the hashlock
     */
    function withdraw(bytes32 _contractId, string memory _preimage)
        public
        contractExists(_contractId)
        hashlockMatches(_contractId, _preimage)
        withdrawable(_contractId)
        nonReentrant
    {
        HTLC storage htlc = contracts[_contractId];
        htlc.preimage = _preimage;
        htlc.withdrawn = true;

        if (htlc.tokenContract == address(0)) {
            // ETH withdrawal
            payable(htlc.receiver).transfer(htlc.amount);
        } else {
            // ERC20 withdrawal
            IERC20(htlc.tokenContract).safeTransfer(htlc.receiver, htlc.amount);
        }

        emit HTLCWithdraw(_contractId, htlc.receiver, _preimage);
    }

    /**
     * @dev Refund funds from an expired HTLC
     * @param _contractId ID of the HTLC
     */
    function refund(bytes32 _contractId)
        public
        contractExists(_contractId)
        refundable(_contractId)
        nonReentrant
    {
        HTLC storage htlc = contracts[_contractId];
        htlc.refunded = true;

        if (htlc.tokenContract == address(0)) {
            // ETH refund
            payable(htlc.sender).transfer(htlc.amount);
        } else {
            // ERC20 refund
            IERC20(htlc.tokenContract).safeTransfer(htlc.sender, htlc.amount);
        }

        emit HTLCRefund(_contractId, htlc.sender);
    }

    /**
     * @dev Get contract details
     * @param _contractId ID of the HTLC
     * @return sender Address of the sender
     * @return receiver Address of the receiver
     * @return tokenContract Address of the token contract (address(0) for ETH)
     * @return amount Amount locked in the contract
     * @return hashLock Hash of the secret
     * @return timelock Unix timestamp when the lock expires
     * @return withdrawn Whether the contract has been withdrawn
     * @return refunded Whether the contract has been refunded
     * @return preimage The revealed secret (empty string if not withdrawn)
     */
    function getContract(bytes32 _contractId)
        public
        view
        returns (
            address sender,
            address receiver,
            address tokenContract,
            uint256 amount,
            bytes32 hashLock,
            uint256 timelock,
            bool withdrawn,
            bool refunded,
            string memory preimage
        )
    {
        if (hasContract(_contractId) == false) {
            return (address(0), address(0), address(0), 0, 0, 0, false, false, "");
        }
        HTLC storage htlc = contracts[_contractId];
        return (
            htlc.sender,
            htlc.receiver,
            htlc.tokenContract,
            htlc.amount,
            htlc.hashLock,
            htlc.timelock,
            htlc.withdrawn,
            htlc.refunded,
            htlc.preimage
        );
    }

    /**
     * @dev Get all contract IDs for a user
     * @param _user Address of the user
     * @return contractIds Array of contract IDs associated with the user
     */
    function getUserContracts(address _user) public view returns (bytes32[] memory contractIds) {
        return userContracts[_user];
    }

    /**
     * @dev Check if a contract exists
     * @param _contractId ID of the HTLC
     * @return exists True if contract exists, false otherwise
     */
    function hasContract(bytes32 _contractId) public view returns (bool exists) {
        return contracts[_contractId].sender != address(0);
    }

    /**
     * @dev Get the status of an HTLC
     * @param _contractId ID of the HTLC
     * @return status Contract status: 0=does not exist, 1=active, 2=withdrawn, 3=refunded, 4=expired
     */
    function getContractStatus(bytes32 _contractId) public view returns (uint8 status) {
        if (!hasContract(_contractId)) {
            return 0; // Does not exist
        }

        HTLC storage htlc = contracts[_contractId];

        if (htlc.withdrawn) {
            return 2; // Withdrawn
        }

        if (htlc.refunded) {
            return 3; // Refunded
        }

        if (block.timestamp >= htlc.timelock) {
            return 4; // Expired
        }

        return 1; // Active
    }

    /**
     * @dev Emergency function to recover stuck tokens (only for development)
     * @param _tokenContract Address of the token contract (address(0) for ETH)
     * @param _amount Amount to recover
     * @notice In production, this should be removed or have proper governance
     */
    function emergencyWithdraw(address _tokenContract, uint256 _amount) external {
        require(msg.sender == address(this), "Not authorized"); // Replace with proper access control

        if (_tokenContract == address(0)) {
            payable(msg.sender).transfer(_amount);
        } else {
            IERC20(_tokenContract).safeTransfer(msg.sender, _amount);
        }
    }
}

contracts/TestToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title TestToken
 * @dev ERC20 token for testing cross-chain swaps
 */
contract TestToken is ERC20, Ownable {
    uint8 private _decimals;

    constructor(
        string memory name,
        string memory symbol,
        uint256 initialSupply
    ) ERC20(name, symbol) {
        _decimals = 18;
        _mint(msg.sender, initialSupply);
    }

    function decimals() public view virtual override returns (uint8) {
        return _decimals;
    }

    function mint(address to, uint256 amount) external onlyOwner {
        _mint(to, amount);
    }

    function burn(uint256 amount) external {
        _burn(msg.sender, amount);
    }

    function burnFrom(address account, uint256 amount) external {
        uint256 currentAllowance = allowance(account, msg.sender);
        require(currentAllowance >= amount, "ERC20: burn amount exceeds allowance");
        _approve(account, msg.sender, currentAllowance - amount);
        _burn(account, amount);
    }
}

